import { NgFor } from '@angular/common';
import { Component, Input, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, FormsModule, Validators } from '@angular/forms';
import { Order } from 'src/app/models/order';

import {MatInputModule} from '@angular/material/input';
import {MatSelectModule} from '@angular/material/select';
import {MatFormFieldModule} from '@angular/material/form-field';
import { Customer } from 'src/app/models/customer';
import { CustomersService } from 'src/app/services/customers.service';
import { Router } from '@angular/router';
import { Status } from 'src/app/models/status';
import { StatusesService } from 'src/app/services/statuses.service';

@Component({
  selector: 'app-add-order',
  templateUrl: './add-order.component.html',
  styleUrls: ['./add-order.component.css'],
  //standalone: true,
  //imports: [FormsModule, MatFormFieldModule, MatSelectModule, NgFor, MatInputModule]
})

// @Component({
//   selector: 'select-form-example',
//   templateUrl: 'select-form-example.html',
//   standalone: true,
//   imports: [FormsModule, MatFormFieldModule, MatSelectModule, NgFor, MatInputModule],
// })

 export class AddOrderComponent implements OnInit {
  customers: Customer[] = [];
  statuses: Status[] = [];
  //@Input() newOrder?: Order;
  newOrder = new Order;
  angForm: FormGroup;


  constructor(private customerService: CustomersService, 
              private statusesServer: StatusesService,
              private fb: FormBuilder) {
                this.createForm();
               }

  ngOnInit(): void {
    this.customerService
      .getCustomers()
      .subscribe((result: Customer[]) => { 
        this.customers = result;
      });

    this.statusesServer
      .getStatues()
      .subscribe((result: Status[]) => {
        this.statuses = result;
        console.log(result);
      });

  }

  createForm() {
    this.angForm = this.fb.group({postText: ['', Validators.required]});
  }

  saveOrder(newOrder: Order){
    console.log(newOrder);
  }

  cancel(){}

}
<div class="page" *ngIf="newOrder">
    <div class="menu">
      <button class="border">Orders</button>
      <button class="border">Products</button>
      <button class="border">Customers</button>
    </div>

    <div class="main border">
      <div class="headerOrders">
        <h2>New Order</h2>
      </div>
      <form [form]>
        <p>Order Number: 12</p>
        <p>Order Date: random date</p>
        <!-- <p>Customer:
          <input name="customer1" ng-model="customer1" [(ngModel)]="newOrder.client.customerId" placeholder="IdClient" required/>
          <select name="customer1" ng-model="customer1" [(ngModel)]="newOrder.client.customerId" required>
                        <option value="" [disabled]="true" [selected]="true">Select customer</option>
            <option *ngFor="let customer of customers" value="{{customer.customerId}}">{{ customer.name }}</option>
          </select>
                      <span style="color:red" ng-show="myForm.customer1.$touched && myForm.customer1.$dirty">
                        Username is required.
                      </span>
        </p> -->
        <p>Status:

          <input name="customer1" ng-model="customer1" [(ngModel)]="newOrder.status.statusId" placeholder="Id" required/>
          <select name="customer1" ng-model="customer1" [(ngModel)]="newOrder.status.statusId" required>

            <option *ngFor="let dddd of statuses" value="{{dddd.statusId}}">{{ dddd.name }}</option>
          </select>
        </p>

        <input type="submit" (click)="saveOrder(newOrder)" ng-disabled="myForm.customer.$dirty && myForm.status.$dirty" value="Save">
        <button (click)="cancel()">Cancel</button>
    </form>
    <h1>{{myrForm.myInput.$valid}}</h1>
    </div>
  </div>
