import { Component, Input } from '@angular/core';
import { Order } from 'src/app/models/order';
import { OrdersService } from 'src/app/services/orders.service';


@Component({
  selector: 'main-root',
  templateUrl: './main.component.html',
  styleUrls: ['./main.component.css']
})
export class MainAppComponent {
  title = 'TestTask.UI';
  orders: Order[] = [];


  constructor(private orderService: OrdersService) {}

  ngOnInit() : void{
    // this.orders = this.orderService.getOrders();
    // console.log(this.orders);
    this.orderService
      .getOrders()
      .subscribe((result: Order[]) => { 
        this.orders = result;
       console.log(result);
      });      
  }

}
<div class="page" >
  <div class="menu">
    <a routerLink="/"><button class="border">Orders</button></a>
    <button class="border">Products</button>
    <button class="border">Customers</button>
  </div>
  <div class="main border">
    <div class="headerOrders">
      <h2>Orders</h2>
      <a routerLink="/newOrder"><button class="border">New Order</button></a>
    </div>
    <div>
      <table>
        <thead>
          <th>Order Number</th>
          <th>Customer Name</th>
          <th>Customer Address</th>
          <th>Total Cost</th>
          <th>Status</th>
        </thead>
        <tbody>
          <tr *ngFor="let order of orders">
            <td>{{ order.id }}</td>
            <td>{{ order.client.name }}</td>
            <td>{{ order.client.address }}</td>
            <td>{{ order.totalCost }}</td>
            <td>{{ order.status.name }}</td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
</div>
